import React from 'react'
import { useState } from "react";
import axios from "axios";

const SignupPage = () => {
    const[fname,setFname]=useState(null)
    const[lname,setLname]=useState(null)
    const[password,setPassword]=useState(null)
    const[phone,setPhone]=useState(null)

    const handleSubmit = async (event) => {
      event.preventDefault(); 

      try {
          const response = await axios.post('http://localhost:3008/signup', {
              fname,
              lname,
              password,
              phone
          });

      console.log('Success',response.data);
      if(response){
        localStorage.setItem("phone",phone)
      }
      if(response.data.status === 'success'){
        alert("Your account has been created successfully.");
      } else {
          alert('There was an error. Please try again.');
      }
  } catch (error) {
      console.error('Error:', error);
      alert('There was an error. Please try again.');
  }
};


  return (<div className="col-5 mx-auto p-3 m-5 rounded border">
            <center><h4>Signin</h4></center>
            <form onSubmit={handleSubmit}>
              <input onChange={(event)=>{setFname(event.target.value)}} className="in rounded form-cotrol mt-3 w-100" name='fname' placeholder='First Name' type='text' required/>
              <input onChange={(event)=>{setLname(event.target.value)}} className="in rounded form-cotrol mt-3 w-100" name='lname' placeholder='Last Name' type='text' required/>
              <input onChange={(event)=>{setPhone(event.target.value)}} className="in rounded form-cotrol mt-3 w-100" name='phone' placeholder='+91'></input>
              <input onChange={(event)=>{setPassword(event.target.value)}} placeholder="password" className="form-cotrol mt-3 w-100"></input>
              <button class="btn btn-warning w-100 mt-3" type="submit">Signin</button>
            </form>
          </div>)
}

export default SignupPage
