import React, { useState } from 'react'
import PersonOutlineOutlinedIcon from '@mui/icons-material/PersonOutlineOutlined';
import ShoppingCartOutlinedIcon from '@mui/icons-material/ShoppingCartOutlined';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import { Link } from 'react-router-dom'
import styled from 'styled-components';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';


const Header = () => {

    const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

return(

<Design>


<div>
    <div className='header text-white'>
      <div className='header-space d-flex mx-auto pt-1'>
      <p>Asia's 1sr Brand with MADE SAFE Certified Products</p>
      <p>Shop for Rs.799 and Get Flat Rs.400 Cashback | SHOP NOW</p>
      <div><PersonOutlineOutlinedIcon></PersonOutlineOutlinedIcon></div>
    </div>
    </div>
      <div className='first-header mx-auto d-flex mt-3'>
         <img className='logo' src="https://images.mamaearth.in/wysiwyg/mamaearth-logo.png?format=auto&fit=scale"></img>
         <div class="search form-control border border d-flex bg-white col-5">
          <div><SearchOutlinedIcon></SearchOutlinedIcon></div>
            <input placeholder="Search" className='border-0 form-control'></input>
            <div class="d-flex w-50 bg-secondary">
              <div><SearchOutlinedIcon></SearchOutlinedIcon></div>
              <p>Search</p>
            </div>
          </div>
          <img className='logo' src="https://images.mamaearth.in/svg/gi-home-button.svg"></img>
          <div className='d-flex'>
            <div><ShoppingCartOutlinedIcon sx={{color:'blue'}}></ShoppingCartOutlinedIcon></div>
            <h6>Cart</h6>
          </div>
          <div className='d-flex'>
            <div><PersonOutlineOutlinedIcon sx={{color:'blue'}}></PersonOutlineOutlinedIcon></div>
            <Button className='bg-transparent border-0 text-dark' onClick={handleShow}>Account</Button>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header>
                    <Modal.Title>Welcome</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <button>
                        <Link to="/login">Login</Link>
                    </button>
                    <p>Already exits</p>
                    <button>
                        <Link to="/signup">Signup</Link>
                        
                    </button>
                    <p>Create new account</p>
                </Modal.Body>
               <Modal.Footer>
               <Button variant="secondary" onClick={handleClose}>Close</Button>
               </Modal.Footer>
            </Modal>


          </div>
      </div>
      <hr className='mb-0'></hr>
      <div className='second-header d-flex justify-content-between mx-auto'>
        <p>HOME</p>
        <p>FACE</p>
        <p>HAIR</p>
        <p>MAKEUP</p>
        <p>BODY</p>
        <p>BABY</p>
        <p>COMBOS</p>
        <p>NEW LAUNCHES</p>
        <p>INGREDEINT</p>
        <p>ALL PRODUCTS</p>
        <p>BLOG</p>
        <p>PLANT GOODNESS</p>
        <p>STORE LOCATOR</p>

      </div>

      </div>  
  

</Design>

)
}

export default Header


const Design = styled.div`


.footer{
    height: 950px;
    width: 75%;
}
.case{
    height: 37px;
    width: 50px;
}
.first-footer-left{
    justify-content:center;
    text-align:center;
    width: 100%;
    p{
        font-size: small;
    }
}
.ffl-right{
    width: 50%;
}
.ffl-left{
    width: 50%;
}
.first-footer-right{
    text-align: center;
    p{
        color:rgb(0,175,239);
        border: 1px solid rgb(1, 168, 230);
        width: 30%;
        font-size: larger;
        font-family: Bold;
    }
}
.third-footer{
    width: 90%;
}
.header{
    background-color: rgb(240, 68, 56);
    height: 32px;
}
.header-space{
    width: 95%;
    justify-content: space-between;
}
.first-header{
    height: 50px;
    width: 90%;
    justify-content: space-evenly;
}
.second-header{
    height: 35px;
    width: 90%;
    p{
        font-size: small;
    }
}
.logo{
    width:150px;
    height:33px;
}
.search{
    width: 40%;
    height:35px;
}
.security{
    height: 20px;
}
.edition{
    width: 150px;
}
.online{
    padding-left:320px;
}
.store{
    padding-left: 335px;
}
.app{
    width: 20%;
}
.fourth-footer img{
    height: 23px;
}
.fifth-footer{
   p{
    text-align: center;
   }
}
.final{
    p,h4,h3{
        padding-left: 70px;
        padding-right: 70px;
    }
    padding-top: 45px;
    height: 1000px;
    background-color: rgb(242, 242, 242);
}
.contact-body{
    height: 350px;
    text-align: center;
}
.content-search{
    text-align: center;
    height: 20px;
}
.contact-box{
    width: 85%;
    h6{
        text-align: center;
    }
}
.bg{
    height: 350px;
    background-color: black;
    background-image: url("https://support.mamaearth.in/hc/theming_assets/01HZQ05NBPJANP50ZC3BQ8G6YC");
}
.box{
    margin-left: 20px;
    margin-right: auto;
    border-radius: 10px;
    border-color: 1px rgba(0, 174, 239, 1);
    color: rgba(0, 174, 239, 1);
}
.recent{
    width: 90%;
}
.act{
    text-align: center;
}
.left{
    width: 30%;
}
.middle{
    width: 40%;
}
.right{
    width: 30%;
}

`
